# Generated by 02-duckplyr_df-methods.R
#' @importFrom dplyr count
#' @export
count.duckplyr_df <- function(x, ..., wt = NULL, sort = FALSE, name = NULL, .drop = group_by_drop_default(x)) {
  # Our implementation
  force(x)

  dplyr_local_error_call()

  quos <- enquos(...)
  exprs <- unname(map(quos, quo_get_expr))
  is_name <- map_lgl(exprs, is_symbol)

  if (all(is_name) && .drop && !sort) {
    by_chr <- map_chr(exprs, as_string)
    name <- check_name(name, by_chr)

    if (!(name %in% by_chr)) {
      n <- tally_n(x, {{ wt }})

      out <- summarise(x, !!name := !!n, .by = c(!!!exprs))
      out <- dplyr_reconstruct(out, x)
      return(out)
    }
  }

  x_df <- x
  class(x_df) <- "data.frame"
  out <- count(x_df, !!!quos, wt = {{ wt }}, sort = sort, name = name, .drop = .drop)
  class(out) <- class(x)
  return(out)

  # dplyr implementation
  if (!missing(...)) {
    out <- group_by(x, ..., .add = TRUE, .drop = .drop)
  } else {
    out <- x
  }

  out <- tally(out, wt = !!enquo(wt), sort = sort, name = name)

  # Ensure grouping is transient
  out <- dplyr_reconstruct(out, x)

  out
}

duckplyr_count <- function(x, ...) {
  try_fetch(
    x <- as_duckplyr_df(x),
    error = function(e) {
      testthat::skip(conditionMessage(e))
    }
  )
  out <- count(x, ...)
  class(out) <- setdiff(class(out), "duckplyr_df")
  out
}
