#' @importFrom dplyr full_join
#' @export
full_join.duckplyr_df <- function(x, y, by = NULL, copy = FALSE, suffix = c(".x", ".y"), ..., keep = NULL, na_matches = c("na", "never"), multiple = NULL) {
  # Our implementation
  force(x)
  out <- NextMethod()
  out <- dplyr_reconstruct(out, x)
  return(out)

  # dplyr implementation
  y <- auto_copy(x, y, copy = copy)
  join_mutate(
    x = x,
    y = y,
    by = by,
    type = "full",
    suffix = suffix,
    na_matches = na_matches,
    keep = keep,
    multiple = multiple,
    # All keys from both inputs are retained. Erroring never makes sense.
    unmatched = "drop",
    user_env = caller_env()
  )
}

duckplyr_full_join <- function(.data, ...) {
  if (is_grouped_df(.data)) {
    testthat::skip("`full_join()` not supported for grouped_df")
  }

  if (inherits(.data, "rowwise_df")) {
    testthat::skip("`full_join()` not supported for rowwise_df")
  }

  .data <- as_duckplyr_df(.data)
  out <- full_join(.data, ...)
  class(out) <- setdiff(class(out), "duckplyr_df")
  out
}
