#' @importFrom dplyr select
#' @export
select.duckplyr_df <- function(.data, ...) {
  force(.data)

  error_call <- dplyr_error_call()

  loc <- tidyselect::eval_select(
    expr(c(...)),
    data = .data,
    error_call = error_call
  )

  # Our implementation
  loc_name <- names(.data)[loc]
  exprs <- map2(names(.data)[loc], names(loc), ~ relexpr_reference(.x, alias = .y))

  # Ensure `select()` appears in call stack
  select <- rel_try
  select(
    "Can't use relational with zero-column result set." = (length(loc) == 0),
    {
      rel <- duckdb_rel_from_df(.data)
    }, fallback = {
      out <- NextMethod()
      out <- dplyr_reconstruct(out, .data)
      return(out)
    }
  )

  out_rel <- rel_project(rel, exprs)
  out <- rel_to_df(out_rel)
  out <- dplyr_reconstruct(out, .data)
  return(out)

  # dplyr implementation
  # duckplyr: Common code
  error_call <- dplyr_error_call()

  loc <- tidyselect::eval_select(
    expr(c(...)),
    data = .data,
    error_call = error_call
  )
  loc <- ensure_group_vars(loc, .data, notify = TRUE)

  # duckplyr: Backend-specific
  out <- dplyr_col_select(.data, loc)
  out <- set_names(out, names(loc))

  out
}

duckplyr_select <- function(.data, ...) {
  try_fetch(
    .data <- as_duckplyr_df(.data),
    error = function(e) {
      testthat::skip(conditionMessage(e))
    }
  )
  out <- select(.data, ...)
  class(out) <- setdiff(class(out), "duckplyr_df")
  out
}
