# Generated by 02-duckplyr_df-methods.R
#' @importFrom dplyr symdiff
#' @export
symdiff.duckplyr_df <- function(x, y, ...) {
  # Our implementation
  force(x)
  out <- NextMethod()
  out <- dplyr_reconstruct(out, x)
  return(out)

  # dplyr implementation
  check_dots_empty()
  check_compatible(x, y)

  cast <- vec_cast_common(x = x, y = y)
  only_x <- vec_slice(cast$x, !vec_in(cast$x, cast$y))
  only_y <- vec_slice(cast$y, !vec_in(cast$y, cast$x))

  out <- vec_unique(vec_rbind(only_x, only_y))
  dplyr_reconstruct(out, x)
}

duckplyr_symdiff <- function(.data, ...) {
  if (is_grouped_df(.data)) {
    testthat::skip("`symdiff()` not supported for grouped_df")
  }

  if (inherits(.data, "rowwise_df")) {
    testthat::skip("`symdiff()` not supported for rowwise_df")
  }

  .data <- as_duckplyr_df(.data)
  out <- symdiff(.data, ...)
  class(out) <- setdiff(class(out), "duckplyr_df")
  out
}
