# Generated by 02-duckplyr_df-methods.R
#' @importFrom dplyr union
#' @export
union.duckplyr_df <- function(x, y, ...) {
  # Our implementation
  force(x)
  out <- NextMethod()
  out <- dplyr_reconstruct(out, x)
  return(out)

  # dplyr implementation
  check_dots_empty()
  check_compatible(x, y)

  out <- vec_unique(vec_rbind(x, y))
  dplyr_reconstruct(out, x)
}

duckplyr_union <- function(x, y, ...) {
  if (!identical(class(x), "data.frame") && !identical(class(x), c("tbl_df", "tbl", "data.frame"))) {
    testthat::skip("`union()` only supported for plain data frames or tibbles")
  }

  x <- as_duckplyr_df(x)
  y <- as_duckplyr_df(y)
  out <- union(x, y, ...)
  class(out) <- setdiff(class(out), "duckplyr_df")
  out
}
