# Generated by 03-tests.R
test_that("as_duckplyr_df() commutes for do()", {
  # Data
  test_df <- data.frame(a = 1, b = 2)

  # Run
  pre <- test_df %>% as_duckplyr_df() %>% do(data.frame(c = 1))
  post <- test_df %>% do(data.frame(c = 1)) %>% as_duckplyr_df()

  # Compare
  expect_equal(pre, post)
})
